mapper.update:
update mdl_b_mdt
team_name=#{teamName}, dept_head=#{deptHead}, user_account_name_leader=#{userAccountNameLeader}, user_account_leader=#{userAccountLeader}, user_account_name_assist=#{userAccountNameAssist}, user_account_name_leader2=#{userAccountNameLeader2}, user_account_leader2=#{userAccountLeader2}, user_account_name_assist2=#{userAccountNameAssist2}, user_account_assist2=#{userAccountAssist2}, user_account_assist=#{userAccountAssist}, state=#{state}, start_date=#{startDate}, end_date=#{endDate}, note=#{note}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from mdl_b_mdt
where  1=1
     and  mdl_b_mdt.team_name like concat('%', #{ mdlBMdt.teamName} ,'%')
     and  mdl_b_mdt.dept_head like concat('%', #{ mdlBMdt.deptHead} ,'%')
     and  mdl_b_mdt.user_account_name_leader like concat('%', #{ mdlBMdt.userAccountNameLeader} ,'%')
     and  mdl_b_mdt.user_account_leader like concat('%', #{ mdlBMdt.userAccountLeader} ,'%')
     and  mdl_b_mdt.user_account_name_assist like concat('%', #{ mdlBMdt.userAccountNameAssist} ,'%')
     and  mdl_b_mdt.user_account_name_leader2 like concat('%', #{ mdlBMdt.userAccountNameLeader2} ,'%')
     and  mdl_b_mdt.user_account_leader2 like concat('%', #{ mdlBMdt.userAccountLeader2} ,'%')
     and  mdl_b_mdt.user_account_name_assist2 like concat('%', #{ mdlBMdt.userAccountNameAssist2} ,'%')
     and  mdl_b_mdt.user_account_assist2 like concat('%', #{ mdlBMdt.userAccountAssist2} ,'%')
     and  mdl_b_mdt.user_account_assist like concat('%', #{ mdlBMdt.userAccountAssist} ,'%')
 and  mdl_b_mdt.state =#{ mdlBMdt.state} 

    AND mdl_b_mdt.start_date  >=   #{mdlBMdt.startDateFrom}


    AND  mdl_b_mdt.start_date  <=   #{mdlBMdt.startDateTo}


    AND mdl_b_mdt.end_date  >=   #{mdlBMdt.endDateFrom}


    AND  mdl_b_mdt.end_date  <=   #{mdlBMdt.endDateTo}

     and  mdl_b_mdt.note like concat('%', #{ mdlBMdt.note} ,'%')
 and  mdl_b_mdt.IS_DELETEMARK =#{ mdlBMdt.isDeletemark} 

    AND mdl_b_mdt.CREATE_TIME  >=   #{mdlBMdt.createTimeFrom}


    AND  mdl_b_mdt.CREATE_TIME  <=   #{mdlBMdt.createTimeTo}


    AND mdl_b_mdt.MODIFY_TIME  >=   #{mdlBMdt.modifyTimeFrom}


    AND  mdl_b_mdt.MODIFY_TIME  <=   #{mdlBMdt.modifyTimeTo}
