mapper.select:
select * from sdl_b_deptchange where 1=1 and sdl_b_deptchange.id =#{ sdlBDeptchange.id} and sdl_b_deptchange.user_account_name like concat('%', #{ sdlBDeptchange.userAccountName} ,'%') and sdl_b_deptchange.user_account like concat('%', #{ sdlBDeptchange.userAccount} ,'%') and sdl_b_deptchange.operation_name like concat('%', #{ sdlBDeptchange.operationName} ,'%') and sdl_b_deptchange.bq_name like concat('%', #{ sdlBDeptchange.bqName} ,'%') and sdl_b_deptchange.bq_new_name like concat('%', #{ sdlBDeptchange.bqNewName} ,'%') and sdl_b_deptchange.user_type =#{ sdlBDeptchange.userType} and sdl_b_deptchange.dept_id like concat('%', #{ sdlBDeptchange.deptId} ,'%') and sdl_b_deptchange.dept_new_id like concat('%', #{ sdlBDeptchange.deptNewId} ,'%') and sdl_b_deptchange.telephone like concat('%', #{ sdlBDeptchange.telephone} ,'%') and sdl_b_deptchange.dept_name like concat('%', #{ sdlBDeptchange.deptName} ,'%') and sdl_b_deptchange.dept_new_name like concat('%', #{ sdlBDeptchange.deptNewName} ,'%') and sdl_b_deptchange.position_name like concat('%', #{ sdlBDeptchange.positionName} ,'%') and sdl_b_deptchange.np_position_name like concat('%', #{ sdlBDeptchange.npPositionName} ,'%') and sdl_b_deptchange.sex_name like concat('%', #{ sdlBDeptchange.sexName} ,'%') and sdl_b_deptchange.birthday like concat('%', #{ sdlBDeptchange.birthday} ,'%') and sdl_b_deptchange.school_date like concat('%', #{ sdlBDeptchange.schoolDate} ,'%') and sdl_b_deptchange.zyjsgw like concat('%', #{ sdlBDeptchange.zyjsgw} ,'%') and sdl_b_deptchange.zyjsgw_lc like concat('%', #{ sdlBDeptchange.zyjsgwLc} ,'%') and sdl_b_deptchange.xcszyjzc like concat('%', #{ sdlBDeptchange.xcszyjzc} ,'%') and sdl_b_deptchange.appointed_date like concat('%', #{ sdlBDeptchange.appointedDate} ,'%') and sdl_b_deptchange.patent_ranknum =#{ sdlBDeptchange.patentRanknum} and sdl_b_deptchange.is_authority like concat('%', #{ sdlBDeptchange.isAuthority} ,'%') and sdl_b_deptchange.file_id like concat('%', #{ sdlBDeptchange.fileId} ,