AND sdl_b_deptchange.CREATE_TIME >= #{sdlBDeptchange.createTimeFrom}
AND sdl_b_deptchange.CREATE_TIME <= #{sdlBDeptchange.createTimeTo}
and sdl_b_deptchange.display_index =#{ sdlBDeptchange.displayIndex}
AND sdl_b_deptchange.MODIFY_TIME >= #{sdlBDeptchange.modifyTimeFrom}
AND sdl_b_deptchange.MODIFY_TIME <= #{sdlBDeptchange.modifyTimeTo}
