and sdl_b_deptchange.auditSuggestion like concat('%', #{ sdlBDeptchange.auditSuggestion} ,'%') and sdl_b_deptchange.appointed_date_lc like concat('%', #{ sdlBDeptchange.appointedDateLc} ,'%') and sdl_b_deptchange.file_id_lc like concat('%', #{ sdlBDeptchange.fileIdLc} ,'%') and sdl_b_deptchange.file_url_lc like concat('%', #{ sdlBDeptchange.fileUrlLc} ,'%') and sdl_b_deptchange.yuangongzu like concat('%', #{ sdlBDeptchange.yuangongzu} ,'%') and sdl_b_deptchange.xrgwjb like concat('%', #{ sdlBDeptchange.xrgwjb} ,'%') and sdl_b_deptchange.xrgwjbprsj like concat('%', #{ sdlBDeptchange.xrgwjbprsj} ,'%') and sdl_b_deptchange.djrdzzw like concat('%', #{ sdlBDeptchange.djrdzzw} ,'%') and sdl_b_deptchange.chujikh_date like concat('%', #{ sdlBDeptchange.chujikhDate} ,'%') and sdl_b_deptchange.zhongjikh_date like concat('%', #{ sdlBDeptchange.zhongjikhDate} ,'%') and sdl_b_deptchange.political_status like concat('%', #{ sdlBDeptchange.politicalStatus} ,'%') and sdl_b_deptchange.staff_grade like concat('%', #{ sdlBDeptchange.staffGrade} ,'%') and sdl_b_deptchange.id_card like concat('%', #{ sdlBDeptchange.idCard} ,'%') and sdl_b_deptchange.staff_date like concat('%', #{ sdlBDeptchange.staffDate} ,'%') and sdl_b_deptchange.picture_id like concat('%', #{ sdlBDeptchange.pictureId} ,'%') and sdl_b_deptchange.picture_url like concat('%', #{ sdlBDeptchange.pictureUrl} ,'%') and sdl_b_deptchange.gqpxqk like concat('%', #{ sdlBDeptchange.gqpxqk} ,'%') and sdl_b_deptchange.gfhyspxqk like concat('%', #{ sdlBDeptchange.gfhyspxqk} ,'%') and sdl_b_deptchange.zjspnlceqk like concat('%', #{ sdlBDeptchange.zjspnlceqk} ,'%') and sdl_b_deptchange.dca_year like concat('%', #{ sdlBDeptchange.dcaYear} ,'%')
