mapper.update:
update sdl_b_schedule_detail
base_id=#{baseId}, user_no=#{userNo}, name=#{name}, user_name=#{userName}, dept_name=#{deptName}, yq_name=#{yqName}, bq_name=#{bqName}, dept_id=#{deptId}, yq_id=#{yqId}, bq_id=#{bqId}, schedule_date=#{scheduleDate}, banci_id=#{banciId}, banci=#{banci}, user_account=#{userAccount}, user_account_name=#{userAccountName}, rylx_id=#{rylxId}, rylx_name=#{rylxName}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from sdl_b_schedule_detail
where  1=1
     and  sdl_b_schedule_detail.id =#{ sdlBScheduleDetail.id} 
     and  sdl_b_schedule_detail.base_id like concat('%', #{ sdlBScheduleDetail.baseId} ,'%')
     and  sdl_b_schedule_detail.user_no like concat('%', #{ sdlBScheduleDetail.userNo} ,'%')
     and  sdl_b_schedule_detail.name like concat('%', #{ sdlBScheduleDetail.name} ,'%')
     and  sdl_b_schedule_detail.user_name like concat('%', #{ sdlBScheduleDetail.userName} ,'%')
     and  sdl_b_schedule_detail.dept_name like concat('%', #{ sdlBScheduleDetail.deptName} ,'%')
     and  sdl_b_schedule_detail.yq_name like concat('%', #{ sdlBScheduleDetail.yqName} ,'%')
     and  sdl_b_schedule_detail.bq_name like concat('%', #{ sdlBScheduleDetail.bqName} ,'%')
     and  sdl_b_schedule_detail.dept_id like concat('%', #{ sdlBScheduleDetail.deptId} ,'%')
 and  sdl_b_schedule_detail.yq_id =#{ sdlBScheduleDetail.yqId} 
     and  sdl_b_schedule_detail.bq_id like concat('%', #{ sdlBScheduleDetail.bqId} ,'%')

    AND sdl_b_schedule_detail.schedule_date  >=   #{sdlBScheduleDetail.scheduleDateFrom}


    AND  sdl_b_schedule_detail.schedule_date  <=   #{sdlBScheduleDetail.scheduleDateTo}

 and  sdl_b_schedule_detail.banci_id =#{ sdlBScheduleDetail.banciId} 
     and  sdl_b_schedule_detail.banci like concat('%', #{ sdlBScheduleDetail.banci} ,'%')
     and  sdl_b_schedule_detail.user_account like concat('%', #{ sdlBScheduleDetail.userAccount} ,'%')
     and  sdl_b_schedule_detail.user_account_name like concat('%', #{ sdlBScheduleDetail.userAccountName} ,'%')
 and  sdl_b_schedule_detail.rylx_id =#{ sdlBScheduleDetail.rylxId} 
 and  sdl_b_schedule_detail.rylx_name =#{ sdlBScheduleDetail.rylxName} 
 and  sdl_b_schedule_detail.state =#{ sdlBScheduleDetail.state} 
 and  sdl_b_schedule_detail.IS_DELETEMARK =#{ sdlBScheduleDetail.isDeletemark} 

    AND sdl_b_schedule_detail.CREATE_TIME  >=   #{sdlBScheduleDetail.createTimeFrom}


    AND  sdl_b_schedule_detail.CREATE_TIME  <=   #{sdlBScheduleDetail.createTimeTo}


    AND sdl_b_schedule_detail.MODIFY_TIME  >=   #{sdlBScheduleDetail.modifyTimeFrom}


    AND  sdl_b_schedule_detail.MODIFY_TIME  <=   #{sdlBScheduleDetail.modifyTimeTo}

 and  sdl_b_schedule_detail.CREATE_USER_ID =#{ sdlBScheduleDetail.createUserId} 
 and  sdl_b_schedule_detail.MODIFY_USER_ID =#{ sdlBScheduleDetail.modifyUserId}
