mapper.update:
update sdl_b_user
user_account_name=#{userAccountName}, user_account=#{userAccount}, bq_name=#{bqName}, user_type=#{userType}, dept_id=#{deptId}, telephone=#{telephone}, dept_name=#{deptName}, position_name=#{positionName}, np_position_name=#{npPositionName}, sex_name=#{sexName}, birthday=#{birthday}, school_date=#{schoolDate}, zyjsgw=#{zyjsgw}, zyjsgw_lc=#{zyjsgwLc}, xcszyjzc=#{xcszyjzc}, appointed_date=#{appointedDate}, patent_ranknum=#{patentRanknum}, is_authority=#{isAuthority}, file_id=#{fileId}, is_zhuanrang=#{isZhuanrang}, file_url=#{fileUrl}, patent_good=#{patentGood}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, display_index=#{displayIndex}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, appointed_date_lc=#{appointedDateLc}, file_id_lc=#{fileIdLc}, file_url_lc=#{fileUrlLc}, yuangongzu=#{yuangongzu}, xrgwjb=#{xrgwjb}, xrgwjbprsj=#{xrgwjbprsj}, djrdzzw=#{djrdzzw}, is_chujikh=#{isChujikh}, chujikh_date=#{chujikhDate}, is_zhongjikh=#{isZhongjikh}, zhongjikh_date=#{zhongjikhDate}, political_status=#{politicalStatus}, staff_grade=#{staffGrade}, id_card=#{idCard}, staff_date=#{staffDate}, picture_id=#{pictureId}, picture_url=#{pictureUrl}, gqpxqk=#{gqpxqk}, gfhyspxqk=#{gfhyspxqk}, zjspnlceqk=#{zjspnlceqk}, dca_year=#{dcaYear},
where id=#{id}

mapper.select:
select * from sdl_b_user where 1=1 and sdl_b_user.id =#{ sdlBUser.id} and sdl_b_user.user_account_name like concat('%', #{ sdlBUser.userAccountName} ,'%') and sdl_b_user.user_account like concat('%', #{ sdlBUser.userAccount} ,'%') and sdl_b_user.bq_name like concat('%', #{ sdlBUser.bqName} ,'%') and sdl_b_user.user_type =#{ sdlBUser.userType} and sdl_b_user.dept_id like concat('%', #{ sdlBUser.deptId} ,'%') and sdl_b_user.telephone like concat('%', #{ sdlBUser.telephone} ,'%') and sdl_b_user.dept_name like concat('%', #{ sdlBUser.deptName} ,'%') and sdl_b_user.position_name like concat('%', #{ sdlBUser.positionName} ,'%') and sdl_b_user.np_position_name like concat('%', #{ sdlBUser.npPositionName} ,'%') and sdl_b_user.sex_name like concat('%', #{ sdlBUser.sexName} ,'%') and sdl_b_user.birthday like concat('%', #{ sdlBUser.birthday} ,'%') and sdl_b_user.school_date like concat('%', #{ sdlBUser.schoolDate} ,'%') and sdl_b_user.zyjsgw like concat('%', #{ sdlBUser.zyjsgw} ,'%') and sdl_b_user.zyjsgw_lc like concat('%', #{ sdlBUser.zyjsgwLc} ,'%') and sdl_b_user.xcszyjzc like concat('%', #{ sdlBUser.xcszyjzc} ,'%') and sdl_b_user.appointed_date like concat('%', #{ sdlBUser.appointedDate} ,'%') and sdl_b_user.patent_ranknum =#{ sdlBUser.patentRanknum} and sdl_b_user.is_authority like concat('%', #{ sdlBUser.isAuthority} ,'%') and sdl_b_user.file_id like concat('%', #{ sdlBUser.fileId} ,'%') and sdl_b_user.is_zhuanrang like concat('%', #{ sdlBUser.isZhuanrang} ,'%') and sdl_b_user.file_url like concat('%', #{ sdlBUser.fileUrl} ,'%') and sdl_b_user.patent_good like concat('%', #{ sdlBUser.patentGood} ,'%') and sdl_b_user.state =#{ sdlBUser.state} and sdl_b_user.IS_DELETEMARK =#{ sdlBUser.isDeletemark}
AND sdl_b_user.CREATE_TIME >= #{sdlBUser.createTimeFrom}
AND sdl_b_user.CREATE_TIME <= #{sdlBUser.createTimeTo}
and sdl_b_user.display_index =#{ sdlBUser.displayIndex}
AND sdl_b_user.MODIFY_TIME >= #{sdlBUser.modifyTimeFrom}
AND sdl_b_user.MODIFY_TIME <= #{sdlBUser.modifyTimeTo}
and sdl_b_user.auditMan like concat('%', #{ sdlBUser.auditMan} ,'%') and sdl_b_user.auditManName like concat('%', #{ sdlBUser.auditManName} ,'%')
AND sdl_b_user.auditDate >= #{sdlBUser.auditDateFrom}
AND sdl_b_user.auditDate <= #{sdlBUser.auditDateTo}
and sdl_b_user.auditSuggestion like concat('%', #{ sdlBUser.auditSuggestion} ,'%') and sdl_b_user.appointed_date_lc like concat('%', #{ sdlBUser.appointedDateLc} ,'%') and sdl_b_user.file_id_lc like concat('%', #{ sdlBUser.fileIdLc} ,'%') and sdl_b_user.file_url_lc like concat('%', #{ sdlBUser.fileUrlLc} ,'%') and sdl_b_user.yuangongzu like concat('%', #{ sdlBUser.yuangongzu} ,'%') and sdl_b_user.xrgwjb like concat('%', #{ sdlBUser.xrgwjb} ,'%') and sdl_b_user.xrgwjbprsj like concat('%', #{ sdlBUser.xrgwjbprsj} ,'%') and sdl_b_user.djrdzzw like concat('%', #{ sdlBUser.djrdzzw} ,'%') and sdl_b_user.chujikh_date like concat('%', #{ sdlBUser.chujikhDate} ,'%') and sdl_b_user.zhongjikh_date like concat('%', #{ sdlBUser.zhongjikhDate} ,'%') and sdl_b_user.political_status like concat('%', #{ sdlBUser.politicalStatus} ,'%') and sdl_b_user.staff_grade like concat('%', #{ sdlBUser.staffGrade} ,'%') and sdl_b_user.id_card like concat('%', #{ sdlBUser.idCard} ,'%') and sdl_b_user.staff_date like concat('%', #{ sdlBUser.staffDate} ,'%') and sdl_b_user.picture_id like concat('%', #{ sdlBUser.pictureId} ,'%') and sdl_b_user.picture_url like concat('%', #{ sdlBUser.pictureUrl} ,'%') and sdl_b_user.gqpxqk like concat('%', #{ sdlBUser.gqpxqk} ,'%') and sdl_b_user.gfhyspxqk like concat('%', #{ sdlBUser.gfhyspxqk} ,'%') and sdl_b_user.zjspnlceqk like concat('%', #{ sdlBUser.zjspnlceqk} ,'%') and sdl_b_user.dca_year like concat('%', #{ sdlBUser.dcaYear} ,'%')
