mapper.update:
update sdl_b_zizhiapply
user_no=#{userNo}, name=#{name}, user_name=#{userName}, user_account=#{userAccount}, user_account_name=#{userAccountName}, rylx_id=#{rylxId}, rylx_name=#{rylxName}, dept_name=#{deptName}, bq_name=#{bqName}, dept_id=#{deptId}, apply_date=#{applyDate}, apply_type=#{applyType}, school_date=#{schoolDate}, position_name=#{positionName}, position_prdate=#{positionPrdate}, orign_type=#{orignType}, new_type=#{newType}, note=#{note}, audit_suggestion=#{auditSuggestion}, audit_date=#{auditDate}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from sdl_b_zizhiapply
where  1=1
     and  sdl_b_zizhiapply.id =#{ sdlBZizhiapply.id} 
     and  sdl_b_zizhiapply.user_no like concat('%', #{ sdlBZizhiapply.userNo} ,'%')
     and  sdl_b_zizhiapply.name like concat('%', #{ sdlBZizhiapply.name} ,'%')
     and  sdl_b_zizhiapply.user_name like concat('%', #{ sdlBZizhiapply.userName} ,'%')
     and  sdl_b_zizhiapply.user_account like concat('%', #{ sdlBZizhiapply.userAccount} ,'%')
     and  sdl_b_zizhiapply.user_account_name like concat('%', #{ sdlBZizhiapply.userAccountName} ,'%')
 and  sdl_b_zizhiapply.rylx_id =#{ sdlBZizhiapply.rylxId} 
     and  sdl_b_zizhiapply.rylx_name like concat('%', #{ sdlBZizhiapply.rylxName} ,'%')
     and  sdl_b_zizhiapply.dept_name like concat('%', #{ sdlBZizhiapply.deptName} ,'%')
     and  sdl_b_zizhiapply.bq_name like concat('%', #{ sdlBZizhiapply.bqName} ,'%')
     and  sdl_b_zizhiapply.dept_id like concat('%', #{ sdlBZizhiapply.deptId} ,'%')

    AND sdl_b_zizhiapply.apply_date  >=   #{sdlBZizhiapply.applyDateFrom}


    AND  sdl_b_zizhiapply.apply_date  <=   #{sdlBZizhiapply.applyDateTo}

     and  sdl_b_zizhiapply.apply_type like concat('%', #{ sdlBZizhiapply.applyType} ,'%')

    AND sdl_b_zizhiapply.school_date  >=   #{sdlBZizhiapply.schoolDateFrom}


    AND  sdl_b_zizhiapply.school_date  <=   #{sdlBZizhiapply.schoolDateTo}

     and  sdl_b_zizhiapply.position_name like concat('%', #{ sdlBZizhiapply.positionName} ,'%')
     and  sdl_b_zizhiapply.position_prdate like concat('%', #{ sdlBZizhiapply.positionPrdate} ,'%')
     and  sdl_b_zizhiapply.orign_type like concat('%', #{ sdlBZizhiapply.orignType} ,'%')
     and  sdl_b_zizhiapply.new_type like concat('%', #{ sdlBZizhiapply.newType} ,'%')
     and  sdl_b_zizhiapply.note like concat('%', #{ sdlBZizhiapply.note} ,'%')
     and  sdl_b_zizhiapply.audit_suggestion like concat('%', #{ sdlBZizhiapply.auditSuggestion} ,'%')

    AND sdl_b_zizhiapply.audit_date  >=   #{sdlBZizhiapply.auditDateFrom}


    AND  sdl_b_zizhiapply.audit_date  <=   #{sdlBZizhiapply.auditDateTo}

 and  sdl_b_zizhiapply.state =#{ sdlBZizhiapply.state} 
 and  sdl_b_zizhiapply.IS_DELETEMARK =#{ sdlBZizhiapply.isDeletemark} 

    AND sdl_b_zizhiapply.CREATE_TIME  >=   #{sdlBZizhiapply.createTimeFrom}


    AND  sdl_b_zizhiapply.CREATE_TIME  <=   #{sdlBZizhiapply.createTimeTo}


    AND sdl_b_zizhiapply.MODIFY_TIME  >=   #{sdlBZizhiapply.modifyTimeFrom}


    AND  sdl_b_zizhiapply.MODIFY_TIME  <=   #{sdlBZizhiapply.modifyTimeTo}

 and  sdl_b_zizhiapply.CREATE_USER_ID =#{ sdlBZizhiapply.createUserId} 
 and  sdl_b_zizhiapply.MODIFY_USER_ID =#{ sdlBZizhiapply.modifyUserId}
